var stompClient = null;

$("body").ready(
    $.ajax({
        url:"/findSeats",
        dataType:"json",
        success: function(datas){
            var jo = JSON.parse(JSON.stringify(datas))
            console.log(jo)
            var i = 1;
            var str = jo.map((d)=>{
                if(d['pick']==1){
                    return "<img id='img"+d['id']+"' src='data:image/png;base64,'/>"
                } else {
                    return "<img id='img"+d['id']+"' src='data:image/png;base64,'/>"
                }

            })
            $("body").html(str)
            connect();
        }
    }));

function setConnected(connected) {
    $("#connect").prop("disabled", connected);
    $("#disconnect").prop("disabled", !connected);
    if (connected) {
        $("#conversation").show();
    }
    else {
        $("#conversation").hide();
    }
    $("#greetings").html("");
}

function connect() {
    var socket = new SockJS('/gs-guide-websocket');
    stompClient = Stomp.over(socket);
    stompClient.connect({}, function (frame) {
        setConnected(true);
        console.log('Connected: ' + frame);
        stompClient.subscribe('/topic/greetings', function (msg) {
            var seats = JSON.parse(msg.body);
            console.log(seats);
            seats.forEach((seat)=>{
                $('#img'+seat['id']).attr('src','data:image/png;base64,');
            });
        });
    },
    function errorCallBack(error){
        alert("connection close!");
    });
}

function disconnect() {
    if (stompClient !== null) {
        stompClient.disconnect();
    }
    setConnected(false);
    console.log("Disconnected");
}

function sendName() {
    stompClient.send("/app/hello", {}, JSON.stringify({'name': $("#name").val()}));
}

function showGreeting(message) {
    $("#greetings").append("<tr><td>" + message + "</td></tr>");
}

$(function () {
    $("form").on('submit', function (e) {
        e.preventDefault();
    });
    $( "#connect" ).click(function() { connect(); });
    $( "#disconnect" ).click(function() { disconnect(); });
    $( "#send" ).click(function() { sendName(); });
});

